import { useState } from "react";
import {
  FaAngleLeft, FaAngleRight, FaInfo, FaMinusCircle, FaSearch, FaShoppingCart, FaSyncAlt
} from 'react-icons/fa';
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";
import Modal from "../components/modal/Modal";

const products = productsData.products;
const categories = Array.from(new Set(products.map((product) => product.category)));

export default function ProductDisplay() {
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [productIndex, setProductIndex] = useState(0);
  const [accumulatedTotal, setAccumulatedTotal] = useState(0);
  const [selectedImage, setSelectedImage] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [searchRef, setSearchRef] = useState("");

  const filteredProducts = products.filter((product) => product.category === selectedCategory);
  const product = filteredProducts[productIndex];

  const price = product.price;
  const total = quantity * price;

  const sizes = product.skus.map(sku => ({
    size: sku.size,
    quantity: sku.stock
  }));

  const changeCategory = (direction: number) => {
    const currentIndex = categories.indexOf(selectedCategory);
    setSelectedCategory(categories[(currentIndex + direction + categories.length) % categories.length]);
    setProductIndex(0);
    setSelectedImage("");
  };

  const changeProduct = (direction: number) => {
    setAccumulatedTotal(prev => prev + total);

    // Atualiza o índice do produto
    let newProductIndex = (productIndex + direction + filteredProducts.length) % filteredProducts.length;

    // Se chegou no último produto da categoria, muda para a próxima categoria
    if (newProductIndex === 0 && direction === 1) {
      const currentCategoryIndex = categories.indexOf(selectedCategory);
      const nextCategory = categories[(currentCategoryIndex + 1) % categories.length];
      setSelectedCategory(nextCategory);
      setProductIndex(0);
    }
    // Se chegou no primeiro produto da categoria, muda para a categoria anterior
    else if (newProductIndex === filteredProducts.length - 1 && direction === -1) {
      const currentCategoryIndex = categories.indexOf(selectedCategory);
      const prevCategory = categories[(currentCategoryIndex - 1 + categories.length) % categories.length];
      setSelectedCategory(prevCategory);
      setProductIndex(filteredProducts.length - 1);
    } else {
      setProductIndex(newProductIndex);
    }

    setSelectedImage("");
  };

  const filterProductByRef = (ref: string) => {
    if (ref.trim() === "") {
      setIsSearchModalOpen(false); // Fechar o modal se o campo estiver vazio
      return;
    }

    const foundProduct = products.find(product => product.reference === ref); // Verifica se a referência existe

    if (foundProduct) {
      const foundIndex = products.indexOf(foundProduct);
      setProductIndex(foundIndex); // Atualiza o índice do produto encontrado
      setIsSearchModalOpen(false); // Fecha o modal quando o produto for encontrado
    } else {
      setIsSearchModalOpen(false); // Fechar o modal sem mensagem de erro
    }
  };

  return (
    <div className={styles.container}>
      {/* Header */}
      <div className={styles.header}>
        <FaAngleLeft className={styles.back} />
        <div className={styles.category}>
          <FaAngleLeft className={styles.navIcon} onClick={() => changeCategory(-1)} />
          <span>{selectedCategory}</span>
          <FaAngleRight className={styles.navIcon} onClick={() => changeCategory(1)} />
        </div>
        <div className={styles.userIcon}><span>F</span></div>
      </div>

      {/* Imagem Principal */}
      <div className={styles.image_container}>
        <FaAngleLeft className={styles.navIconImage} onClick={() => changeProduct(-1)} />
        <img src={selectedImage || product.images[0].path} alt={product.name} />
        <FaAngleRight className={styles.navIconImage} onClick={() => changeProduct(1)} />
      </div>

      {/* Miniaturas e Ações */}
      <div className={styles.thumbnails}>
        <FaInfo className={styles.icon} onClick={() => setIsModalOpen(true)} />
        <FaSearch className={styles.icon} onClick={() => setIsSearchModalOpen(true)} />
        {product.images.map((image, index) => (
          <img
            key={index}
            src={image.path}
            alt={`Thumbnail ${index}`}
            className={styles.thumbnail}
            onClick={() => setSelectedImage(image.path)}
          />
        ))}
        <FaShoppingCart className={styles.icon} style={{ cursor: "not-allowed" }} />
      </div>

      <span className={styles.illustration}>Preços ilustrativos</span>

      {/* Detalhes do Produto */}
      <div className={styles.details}>
        <div className={styles.info}>
          <div><FaSyncAlt /><span>{product.name}</span></div>
          <div><span>REF:</span><span>{product.reference}</span></div>
          <div><span>R$</span><span>{price}</span></div>
        </div>
        <div className={styles.price}>
          <div>
            <span>Atual</span>
            <div>
              <span>R$ {total}</span>
              <FaMinusCircle />
              <FaPlusCirclE />
            </div>
          </div>
          <div>
            <span>Acumulado</span>
            <div><span>R$ {accumulatedTotal.toFixed(2)}</span></div>
          </div>
        </div>
      </div>

      {/* Seleção de Tamanho */}
      <div className={styles.footer}>
        <div className={styles.size_selector}>
          <div className={styles.size_options}>
            {sizes.map((s) => (
              <div
                key={s.size}
                className={`${styles.size_option} ${s.size === size ? styles.selected : ""}`}
                onClick={() => setSize(s.size)}
              >
                {s.quantity}
                <span className={styles.size_quantity}>{s.size}</span>
              </div>
            ))}
            <span className={styles.equal}> = </span>
            <div className={styles.pack_quantity}>
              <span className={styles.pack_title}>PACK</span>
              <span>18</span>
            </div>
          </div>
        </div>
      </div>

      {/* Modal de Informações */}
      {isModalOpen && (
        <Modal isOpen={isModalOpen} closeModal={() => setIsModalOpen(false)} title="Informações">
          <div className={styles.information}>
            <div className={styles.colors}><p><strong>Cores</strong> {product.colors.join(", ")}</p></div>
            <div><p><strong>Nome:</strong> {product.name}</p></div>
            <div><p><strong>Referência:</strong> {product.reference}</p></div>
            <div><p><strong>Marca:</strong> {product.brand}</p></div>
            <div><p><strong>Categoria:</strong> {product.category}</p></div>
            <div><p><strong>Gênero:</strong> {product.gender}</p></div>
          </div>
        </Modal>
      )}

      {/* Modal de Pesquisa */}
      {isSearchModalOpen && (
        <Modal isOpen={isSearchModalOpen} closeModal={() => setIsSearchModalOpen(false)} title="BUSCAR POR REF">
          <div className={styles.searchContainer}>
            <input
              type="text"
              value={searchRef}
              onChange={(e) => setSearchRef(e.target.value)}
              placeholder="00.00.000"
              className={styles.searchInput}
            />
            <button onClick={() => filterProductByRef(searchRef)} className={styles.searchButton}>Buscar</button>
          </div>
        </Modal>
      )}
    </div>
  );
}
