import { useState } from "react";
import { FaAngleLeft, FaAngleRight, FaInfoCircle, FaSearch, FaShoppingCart, FaSyncAlt } from 'react-icons/fa';
import Modal from "../"; // Importando o componente Modal
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";

// Assume que productsData é importado e tem a estrutura já discutida
const products = productsData.products;

export default function ProductDisplay() {
  const [selectedCategory, setSelectedCategory] = useState<string>("acessórios");
  const [productIndex, setProductIndex] = useState<number>(0);
  const [isInfoModalOpen, setIsInfoModalOpen] = useState<boolean>(false);
  const [isSearchModalOpen, setIsSearchModalOpen] = useState<boolean>(false);
  const [searchRef, setSearchRef] = useState<string>("");

  const filteredProducts = products.filter((product) => product.category === selectedCategory);
  const product = filteredProducts[productIndex];
  
  const openInfoModal = () => setIsInfoModalOpen(true);
  const closeInfoModal = () => setIsInfoModalOpen(false);

  const openSearchModal = () => setIsSearchModalOpen(true);
  const closeSearchModal = () => setIsSearchModalOpen(false);

  const filterProductByRef = (ref: string) => {
    return products.find((p) => p.reference === ref);
  };

  const price = product.price;
  const total = price;

  const sizes = product.skus.map(sku => ({
    size: sku.size,
    quantity: sku.stock
  }));

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <FaAngleLeft className={styles.back} />
        <div className={styles.category}>
          <FaAngleLeft className={styles.navIcon} />
          <span>{selectedCategory}</span>
          <FaAngleRight className={styles.navIcon} />
        </div>
        <div className={styles.userIcon}>
          <span>F</span>
        </div>
      </div>

      <div className={styles.image_container}>
        <FaAngleLeft className={styles.navIcon} />
        <img src={product.images[0].path} alt={product.name} />
        <FaAngleRight className={styles.navIcon} />
      </div>

      <div className={styles.thumbnails}>
        <FaInfoCircle className={styles.icon} onClick={openInfoModal} />
        <FaSearch className={styles.icon} onClick={openSearchModal} />
        {product.images.map((image, index) => (
          <img
            key={index}
            src={image.path}
            alt={`Thumbnail ${index}`}
            className={styles.thumbnail}
            style={{ width: "44px", height: "44px", objectFit: "cover" }}
          />
        ))}
        <FaShoppingCart className={styles.icon} style={{ cursor: "not-allowed" }} />
      </div>

      <span>Preços ilustrativos</span>

      {/* Modal de Informações */}
      <Modal isOpen={isInfoModalOpen} closeModal={closeInfoModal}>
        <h2>Informações</h2>
        <div>
          <h3>Cores</h3>
          <p>{product.colors.join(", ")}</p>
        </div>
        <div>
          <h3>Nome do Produto</h3>
          <p>{product.name}</p>
        </div>
        <div>
          <h3>Referência</h3>
          <p>{product.reference}</p>
        </div>
        <div>
          <h3>Marca</h3>
          <p>{product.brand}</p>
        </div>
        <div>
          <h3>Categoria</h3>
          <p>{product.category}</p>
        </div>
        <div>
          <h3>Gênero</h3>
          <p>{product.gender}</p>
        </div>
      </Modal>

      {/* Modal de Pesquisa */}
      <Modal isOpen={isSearchModalOpen} closeModal={closeSearchModal}>
        <h2>Buscar por REF</h2>
        <input
          type="text"
          value={searchRef}
          onChange={(e) => setSearchRef(e.target.value)}
          placeholder="Digite o REF do produto"
        />
        <button onClick={() => console.log(filterProductByRef(searchRef))}>Buscar</button>
      </Modal>
    </div>
  );
}
