import styles from "./modal.module.css";

interface ModalProps {
    isOpen: boolean;
    closeModal: () => void;
    children: React.ReactNode; // Esse prop vai permitir passar conteúdo dinâmico
}

const Modal: React.FC<ModalProps> = ({ isOpen, closeModal, children }) => {
    if (!isOpen) return null; // Se a modal não estiver aberta, não renderiza nada

    return (
        <div className={styles.modalOverlay}>
            <div className={styles.modalContent}>
                {children} {/* Exibe o conteúdo passado */}
                <button onClick={closeModal}>Fechar</button> {/* Botão de fechar dentro da modal */}
            </div>
        </div>
    );
};


export default Modal;
