import { useState } from "react";
import { FaAngleLeft, FaAngleRight, FaInfoCircle, FaMinusCircle, FaPlusCircle, FaSearch, FaShoppingCart, FaSyncAlt } from 'react-icons/fa';
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";
import Modal from "../components/modal/Modal"; // Importando o componente Modal

// Assume que productsData é importado e tem a estrutura já discutida
const products = productsData.products;

// Pegando as categorias únicas do JSON
const categories = Array.from(new Set(products.map((product) => product.category)));

// Exibindo as categorias no console
console.log(categories);

export default function ProductDisplay() {
  // Estado para categoria (inicialmente será a primeira da lista)
  const [selectedCategory, setSelectedCategory] = useState<string>(categories[0]);

  // Estado para índice do produto na categoria
  const [productIndex, setProductIndex] = useState<number>(0);

  // Filtrando os produtos pela categoria selecionada
  const filteredProducts = products.filter((product) => product.category === selectedCategory);

  // Verifica se existe produtos na categoria antes de acessar
  const product = filteredProducts[productIndex];

  // Funções para navegação entre os produtos dentro da categoria
  const nextProduct = () => {
    if (productIndex < filteredProducts.length - 1) {
      setProductIndex(productIndex + 1);
    } else {
      // Se chegou no último produto, vai para o primeiro da categoria
      setProductIndex(0);
    }
  };

  const prevProduct = () => {
    if (productIndex > 0) {
      setProductIndex(productIndex - 1);
    } else {
      // Se chegou no primeiro produto, vai para o último da categoria
      setProductIndex(filteredProducts.length - 1);
    }
  };

  // Função para mudar a categoria
  const changeCategory = (category: string) => {
    setSelectedCategory(category);
    setProductIndex(0); // Quando mudar de categoria, começa no primeiro produto
  };

  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState<string>("");

  const [isModalOpen, setIsModalOpen] = useState<boolean>(false); // Estado para controlar a abertura da modal
  const [searchRef, setSearchRef] = useState<string>("");

  // A verificação de "product" é feita antes do uso das variáveis
  if (!product) {
    return <div>Produto não encontrado na categoria selecionada</div>;
  }

  const openSearchModal = () => setIsSearchModalOpen(true); // Função para abrir a modal de pesquisa
  const closeSearchModal = () => setIsSearchModalOpen(false); // Função para fechar a modal de pesquisa

  const price = product.price;
  const total = quantity * price;

  const sizes = product.skus.map(sku => ({
    size: sku.size,
    quantity: sku.stock
  }));

  const openModal = () => setIsModalOpen(true);  // Abre a modal
  const closeModal = () => setIsModalOpen(false); // Fecha a modal

  // Função de filtro de produto por REF
  const filterProductByRef = (ref: string) => {
    return products.find((p) => p.reference === ref);
  };

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <FaAngleLeft className={styles.back} />

        {/* Navegação de categoria */}
        <div className={styles.category}>
          <FaAngleLeft
            className={styles.navIcon}
            onClick={() => {
              const currentIndex = categories.indexOf(selectedCategory);
              const prevCategory = categories[(currentIndex - 1 + categories.length) % categories.length];
              changeCategory(prevCategory);
            }}
          />
          <span>{selectedCategory}</span>
          <FaAngleRight
            className={styles.navIcon}
            onClick={() => {
              const currentIndex = categories.indexOf(selectedCategory);
              const nextCategory = categories[(currentIndex + 1) % categories.length];
              changeCategory(nextCategory);
            }}
          />
        </div>

        <div className={styles.userIcon}>
          <span>F</span>
        </div>
      </div>

      {/* Exibindo a imagem do produto */}
      <div className={styles.image_container}>
        <FaAngleLeft className={styles.navIcon} onClick={prevProduct} />
        <img src={product.images[0].path} alt={product.name} />
        <FaAngleRight className={styles.navIcon} onClick={nextProduct} />
      </div>

      <div className={styles.thumbnails}>
        {/* Modal de Informações */}
        <FaInfoCircle className={styles.icon} onClick={openModal} />
        {/* Aba de Pesquisa */}
        <FaSearch className={styles.icon} onClick={() => console.log(filterProductByRef(searchRef))} />

        {/* Imagens dos produtos */}
        {product.images.map((image, index) => (
          <img
            key={index}
            src={image.path}
            alt={`Thumbnail ${index}`}
            className={styles.thumbnail}
            style={{ width: "44px", height: "44px", objectFit: "cover" }}
          />
        ))}

        {/* Carrinho - Não clicável */}
        <FaShoppingCart className={styles.icon} style={{ cursor: "not-allowed" }} />
      </div>

      <span>Preços ilustrativos</span>

      <div className={styles.details}>
        <div className={styles.info}>
          <div>
            <FaSyncAlt />
            <span>{product.name}</span>
          </div>
          <div>
            <span>REF:</span>
            <span>{product.reference}</span>
          </div>
          <div>
            <span>R$</span>
            <span>{price}</span>
          </div>
        </div>
        <div className={styles.price}>
          <div>
            <span>Atual</span>
            <div>
              <span>R$ {total}</span>
              <FaMinusCircle />
              <FaPlusCircle />
            </div>
          </div>
          <div>
            <span>Acumulado</span>
            <div>
              <span>R$ 0,00</span>
            </div>
          </div>
        </div>
      </div>

      <div className={styles.footer}>
        <div className={styles.size_selector}>
          <div className={styles.size_options}>
            {sizes.map((s) => (
              <div key={s.size} className={`${styles.size_option} ${s.size === size ? styles.selected : ""}`} onClick={() => setSize(s.size)}>
                {s.quantity}
                <span className={styles.size_quantity}>{s.size}</span>
              </div>
            ))}
          </div>
        </div>
        <div className={styles.pack}>
          <span>PACK</span>
          <span>18</span>
        </div>
      </div>
      {/* Modal de Informações - Usando o componente Modal importado */}
      {isModalOpen && (
        <Modal isOpen={isModalOpen} closeModal={closeModal}> {/* Passando isOpen e closeModal */}
          <h2>Informações</h2>
          <div>
            <h3>Cores</h3>
            <p>{product.colors.join(", ")}</p>
          </div>
          <div>
            <h3>Nome do Produto</h3>
            <p>{product.name}</p>
          </div>
          <div>
            <h3>Referência</h3>
            <p>{product.reference}</p>
          </div>
          <div>
            <h3>Marca</h3>
            <p>{product.brand}</p>
          </div>
          <div>
            <h3>Categoria</h3>
            <p>{product.category}</p>
          </div>
          <div>
            <h3>Gênero</h3>
            <p>{product.gender}</p>
          </div>
          {/* Botão para fechar a modal, movido para dentro do Modal */}
          {/* <button onClick={closeModal}>Fechar</button> */}
        </Modal>
      )}
    </div>
  );
}
