import { useState } from "react";
import { FaAngleLeft, FaAngleRight, FaShoppingCart } from 'react-icons/fa';
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";  // Dados dos produtos

export default function ProductDisplay() {
  // Estado para armazenar as categorias selecionadas
  const [selectedCategories, setSelectedCategories] = useState<string[]>(["acessórios"]); 
  // Estado para armazenar os produtos selecionados
  const [selectedProducts, setSelectedProducts] = useState<any[]>([]); 
  // Estado para o índice do produto atualmente em exibição
  const [productIndex, setProductIndex] = useState(0);

  // Função para selecionar ou adicionar produtos
  const addProductToSelection = (product: any) => {
    if (!selectedProducts.find(p => p.id === product.id)) {
      setSelectedProducts([...selectedProducts, product]);
    }
  };

  // Funções para navegação entre os produtos
  const nextProduct = () => {
    if (productIndex < selectedProducts.length - 1) {
      setProductIndex(productIndex + 1);
    } else {
      setProductIndex(0); // Volta para o primeiro produto
    }
  };

  const prevProduct = () => {
    if (productIndex > 0) {
      setProductIndex(productIndex - 1);
    } else {
      setProductIndex(selectedProducts.length - 1); // Vai para o último produto
    }
  };

  // Função para atualizar as categorias selecionadas
  const switchCategory = (category: string) => {
    if (!selectedCategories.includes(category)) {
      setSelectedCategories([...selectedCategories, category]);
    } else {
      setSelectedCategories(selectedCategories.filter(cat => cat !== category));
    }
  };

  // Filtrando produtos com base nas categorias selecionadas
  const filteredProducts = productsData.products.filter(product =>
    selectedCategories.includes(product.category)
  );

  const product = selectedProducts[productIndex];

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <FaAngleLeft className={styles.navIcon} />
        
        {/* Navegação de categorias selecionadas */}
        <div className={styles.category}>
          {selectedCategories.map((category, index) => (
            <span key={index}>{category}</span>
          ))}
        </div>

        <div className={styles.userIcon}>
          <span>F</span>
        </div>
      </div>

      {/* Exibindo a imagem do produto */}
      <div className={styles.image_container}>
        <FaAngleLeft className={styles.navIcon} onClick={prevProduct} />
        {product && <img src={product.images[0].path} alt={product.name} />}
        <FaAngleRight className={styles.navIcon} onClick={nextProduct} />
      </div>

      {/* Thumbnails ou ícones para outras interações */}
      <div className={styles.thumbnails}>
        <FaShoppingCart />
      </div>

      {/* Detalhes do produto */}
      <div className={styles.details}>
        <div className={styles.info}>
          <div>
            <span>{product?.name}</span>
          </div>
          <div>
            <span>Preço: R$ {product?.price}</span>
          </div>
        </div>
      </div>

      {/* Exibindo os produtos disponíveis para seleção */}
      <div className={styles.footer}>
        <div>
          {filteredProducts.map((product) => (
            <div key={product.id}>
              <span>{product.name}</span>
              <button onClick={() => addProductToSelection(product)}>Adicionar</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
