import { useState } from "react";
import { FaAngleLeft, FaAngleRight, FaInfoCircle, FaMinusCircle, FaPlusCircle, FaSearch, FaShoppingCart, FaSyncAlt } from 'react-icons/fa';
import Calca from "../assets/calca.jpg";
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";
import { Product, SKU } from "../types/productTypes";

// Assume que productsData é importado e tem a estrutura já discutida
const products = productsData.products;

export default function ProductDisplay() {
  // Estado para categoria
  const [selectedCategory, setSelectedCategory] = useState<string>(""); // Categoria inicial vazia
  const [productIndex, setProductIndex] = useState<number>(0);

  // Pegando as categorias únicas do JSON
  const categories = Array.from(new Set(products.map((product) => product.category)));

  // Função para filtrar produtos pela categoria
  const filteredProducts = products.filter((product) => product.category === selectedCategory);
  const product = filteredProducts[productIndex]; // Produto atual

  // Funções de navegação entre os produtos
  const nextProduct = () => {
    if (productIndex < filteredProducts.length - 1) {
      setProductIndex(productIndex + 1);
    } else {
      setProductIndex(0); // Volta para o primeiro produto
    }
  };

  const prevProduct = () => {
    if (productIndex > 0) {
      setProductIndex(productIndex - 1);
    } else {
      setProductIndex(filteredProducts.length - 1); // Vai para o último produto
    }
  };

  // Função para alterar a categoria
  const changeCategory = (category: string) => {
    setSelectedCategory(category);
    setProductIndex(0); // Reseta para o primeiro produto da nova categoria
  };

  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState<string>("");

  const price = product.price;
  const total = quantity * price;

  const sizes = product.skus.map(sku => ({
    size: sku.size,
    quantity: sku.stock
  }));

  return (
    <div className={styles.container}>
      <div className={styles.header}>
        <FaAngleLeft className={styles.back} />

        {/* Navegação de categorias */}
        <div className={styles.category}>
          {/* Exibe as categorias no cabeçalho */}
          {categories.map((category) => (
            <div 
              key={category} 
              className={`${styles.categoryItem} ${category === selectedCategory ? styles.selected : ''}`} 
              onClick={() => changeCategory(category)}
            >
              {category}
            </div>
          ))}
        </div>

        <div className={styles.userIcon}>
          <span>F</span>
        </div>
      </div>

      {/* Exibindo a imagem do produto */}
      <div className={styles.image_container}>
        <FaAngleLeft className={styles.navIcon} onClick={prevProduct} />
        <img src={product.images[0].path} alt={product.name} />
        <FaAngleRight className={styles.navIcon} onClick={nextProduct} />
      </div>

      <div className={styles.thumbnails}>
        <FaInfoCircle />
        <FaSearch />
        <img src="https://placehold.co/50x50" alt="Thumbnail of a person wearing a blue coat" />
        <img src="https://placehold.co/50x50" alt="Thumbnail of a person wearing a blue coat" />
        <img src="https://placehold.co/50x50" alt="Thumbnail of a person wearing a blue coat" />
        <FaShoppingCart />
      </div>
      <span>Preços ilustrativos</span>

      <div className={styles.details}>
        <div className={styles.info}>
          <div>
            <FaSyncAlt />
            <span>{product.name}</span>
          </div>
          <div>
            <span>REF:</span>
            <span>{product.reference}</span>
          </div>
          <div>
            <span>R$</span>
            {/* <span>{price}</span> */}
          </div>
        </div>
        <div className={styles.price}>
          <div>
            <span>Atual</span>
            <div>
              <span>R$ {total}</span>
              <FaMinusCircle />
              <FaPlusCircle />
            </div>
          </div>
          <div>
            <span>Acumulado</span>
            <div>
              <span>R$ 0,00</span>
            </div>
          </div>
        </div>
      </div>
      <div className={styles.footer}>
        <div className={styles.size_selector}>
          <div className={styles.size_options}>
            {sizes.map((s) => (
              <div key={s.size} className={`${styles.size_option} ${s.size === size ? styles.selected : ""}`} onClick={() => setSize(s.size)}>
                {s.quantity}
                <span className={styles.size_quantity}>{s.size}</span>
              </div>
            ))}
          </div>
        </div>
        <div className={styles.pack}>
          <span>PACK</span>
          <span>18</span>
        </div>
      </div>
    </div>
  );
}
