const addToCart = () => {
  // Evita adicionar o produto se não houver quantidade selecionada.
  if (quantity > 0) {
    const newItem = {
      name: product.name,
      quantity,
      total: total,
      size, // Adiciona o tamanho selecionado
    };

    setCartItems(prevItems => [...prevItems, newItem]);
    setAccumulatedTotal(prev => prev + total); // Atualiza o total acumulado
    setQuantity(0); // Reseta a quantidade
    setSize(""); // Reseta o tamanho
  }
};
