import { useState } from "react";
import { 
  FaAngleLeft, FaAngleRight, FaInfoCircle, FaMinusCircle, 
  FaPlusCircle, FaSearch, FaShoppingCart, FaSyncAlt 
} from 'react-icons/fa';
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";
import Modal from "../components/modal/Modal";

const products = productsData.products;
const categories = Array.from(new Set(products.map((product) => product.category)));

export default function ProductDisplay() {
  const [selectedCategory, setSelectedCategory] = useState(categories[0]);
  const [productIndex, setProductIndex] = useState(0);
  const [accumulatedTotal, setAccumulatedTotal] = useState(0);
  const [selectedImage, setSelectedImage] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState("");
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [searchRef, setSearchRef] = useState("");

  const filteredProducts = products.filter((product) => product.category === selectedCategory);
  const product = filteredProducts[productIndex];

  if (!product) return <div>Produto não encontrado</div>;

  const price = product.price;
  const total = quantity * price;

  const sizes = product.skus.map(sku => ({
    size: sku.size,
    quantity: sku.stock
  }));

  const changeCategory = (direction: number) => {
    const currentIndex = categories.indexOf(selectedCategory);
    setSelectedCategory(categories[(currentIndex + direction + categories.length) % categories.length]);
    setProductIndex(0);
    setSelectedImage("");
  };

  const changeProduct = (direction: number) => {
    setAccumulatedTotal(prev => prev + total);
    setProductIndex((productIndex + direction + filteredProducts.length) % filteredProducts.length);
    setSelectedImage("");
  };

  const filterProductByRef = (ref: string) => {
    const foundIndex = filteredProducts.findIndex(p => p.reference === ref);
    if (foundIndex !== -1) {
      setProductIndex(foundIndex);
    } else {
      alert("Produto não encontrado");
    }
  };

  return (
    <div className={styles.container}>
      {/* Header */}
      <div className={styles.header}>
        <FaAngleLeft className={styles.back} />
        <div className={styles.category}>
          <FaAngleLeft className={styles.navIcon} onClick={() => changeCategory(-1)} />
          <span>{selectedCategory}</span>
          <FaAngleRight className={styles.navIcon} onClick={() => changeCategory(1)} />
        </div>
        <div className={styles.userIcon}><span>F</span></div>
      </div>

      {/* Imagem Principal */}
      <div className={styles.image_container}>
        <FaAngleLeft className={styles.navIconImage} onClick={() => changeProduct(-1)} />
        <img src={selectedImage || product.images[0].path} alt={product.name} />
        <FaAngleRight className={styles.navIconImage} onClick={() => changeProduct(1)} />
      </div>

      {/* Miniaturas e Ações */}
      <div className={styles.thumbnails}>
        <FaInfoCircle className={styles.icon} onClick={() => setIsModalOpen(true)} />
        <FaSearch className={styles.icon} onClick={() => setIsSearchModalOpen(true)} />
        {product.images.map((image, index) => (
          <img 
            key={index} 
            src={image.path} 
            alt={`Thumbnail ${index}`} 
            className={styles.thumbnail} 
            onClick={() => setSelectedImage(image.path)} 
          />
        ))}
        <FaShoppingCart className={styles.icon} style={{ cursor: "not-allowed" }} />
      </div>

      <span className={styles.illustration}>Preços ilustrativos</span>

      {/* Detalhes do Produto */}
      <div className={styles.details}>
        <div className={styles.info}>
          <div><FaSyncAlt /><span>{product.name}</span></div>
          <div><span>REF:</span><span>{product.reference}</span></div>
          <div><span>R$</span><span>{price}</span></div>
        </div>
        <div className={styles.price}>
          <div>
            <span>Atual</span>
            <div>
              <span>R$ {total}</span>
              <FaMinusCircle />
              <FaPlusCircle />
            </div>
          </div>
          <div>
            <span>Acumulado</span>
            <div><span>R$ {accumulatedTotal.toFixed(2)}</span></div>
          </div>
        </div>
      </div>

      {/* Seleção de Tamanho */}
      <div className={styles.footer}>
        <div className={styles.size_selector}>
          <div className={styles.size_options}>
            {sizes.map((s) => (
              <div 
                key={s.size} 
                className={`${styles.size_option} ${s.size === size ? styles.selected : ""}`} 
                onClick={() => setSize(s.size)}
              >
                {s.quantity}
                <span className={styles.size_quantity}>{s.size}</span>
              </div>
            ))}
            <span className={styles.equal}> = </span>
            <div className={styles.pack_quantity}>
              <span className={styles.pack_title}>PACK</span>
              <span>18</span>
            </div>
          </div>
        </div>
      </div>

      {/* Modal de Informações */}
      {isModalOpen && (
        <Modal isOpen={isModalOpen} closeModal={() => setIsModalOpen(false)}>
          <h2>Informações</h2>
          <div><h3>Cores</h3><p>{product.colors.join(", ")}</p></div>
          <div><h3>Nome</h3><p>{product.name}</p></div>
          <div><h3>Referência</h3><p>{product.reference}</p></div>
          <div><h3>Marca</h3><p>{product.brand}</p></div>
          <div><h3>Categoria</h3><p>{product.category}</p></div>
          <div><h3>Gênero</h3><p>{product.gender}</p></div>
        </Modal>
      )}

      {/* Modal de Pesquisa */}
      {isSearchModalOpen && (
        <Modal isOpen={isSearchModalOpen} closeModal={() => setIsSearchModalOpen(false)}>
          <h2>Pesquisar Produto</h2>
          <input 
            type="text" 
            value={searchRef} 
            onChange={(e) => setSearchRef(e.target.value)} 
            placeholder="Digite a referência" 
          />
          <button onClick={() => filterProductByRef(searchRef)}>Pesquisar</button>
        </Modal>
      )}
