import { useState } from "react";
import { FaAngleLeft, FaAngleRight, FaInfoCircle, FaMinusCircle, FaPlusCircle, FaSearch, FaShoppingCart, FaSyncAlt } from 'react-icons/fa';
import Calca from "../assets/calca.jpg";
import styles from "./productDisplay.module.css";
import productsData from "../data/products.json";
import { Product, SKU } from "../types/productTypes";


// Assume que productsData é importado e tem a estrutura já discutida
const products = productsData.products;

export default function ProductDisplay() {


  // Estado para armazenar as categorias selecionadas
  const [selectedCategories, setSelectedCategories] = useState<string[]>(["acessórios"]); 
  // Estado para armazenar os produtos selecionados
  const [selectedProducts, setSelectedProducts] = useState<any[]>([]); 
  // Estado para o índice do produto atualmente em exibição
  const [productIndex, setProductIndex] = useState(0);

  // Função para selecionar ou adicionar produtos
  const addProductToSelection = (product: any) => {
    if (!selectedProducts.find(p => p.id === product.id)) {
      setSelectedProducts([...selectedProducts, product]);
    }
  };

  // Funções para navegação entre os produtos
  const nextProduct = () => {
    if (productIndex < selectedProducts.length - 1) {
      setProductIndex(productIndex + 1);
    } else {
      setProductIndex(0); // Volta para o primeiro produto
    }
  };

  const prevProduct = () => {
    if (productIndex > 0) {
      setProductIndex(productIndex - 1);
    } else {
      setProductIndex(selectedProducts.length - 1); // Vai para o último produto
    }
  };

  // Função para atualizar as categorias selecionadas
  const switchCategory = (category: string) => {
    if (!selectedCategories.includes(category)) {
      setSelectedCategories([...selectedCategories, category]);
    } else {
      setSelectedCategories(selectedCategories.filter(cat => cat !== category));
    }
  };

  // Filtrando produtos com base nas categorias selecionadas
  const filteredProducts = productsData.products.filter(product =>
    selectedCategories.includes(product.category)
  );

  const product = selectedProducts[productIndex];



  const [quantity, setQuantity] = useState(1);
  const [size, setSize] = useState<string>("");


  const price = product.price;
  const total = quantity * price;

  const sizes = product.skus.map(sku => ({
    size: sku.size,
    quantity: sku.stock
  }));

  return (
    <>
      <div className={styles.container}>
        <div className={styles.header}>
          <FaAngleLeft className={styles.back} />


          {/* Navegação de categoria */}
          <div className={styles.category}>
            <FaAngleLeft className={styles.navIcon} onClick={() => changeCategory("acessórios")} />
            <span>{selectedCategory}</span>
            <FaAngleRight className={styles.navIcon} onClick={() => changeCategory("blusas")} />
          </div>


          <div className={styles.userIcon}>
            <span>F</span>
          </div>
        </div>


        {/* Exibindo a imagem do produto */}
        <div className={styles.image_container}>
          <FaAngleLeft className={styles.navIcon} onClick={prevProduct} />
          <img src={product.images[0].path} alt={product.name} />
          <FaAngleRight className={styles.navIcon} onClick={nextProduct} />
        </div>


        <div className={styles.thumbnails}>
          <FaInfoCircle />
          <FaSearch />
          <img src="https://placehold.co/50x50" alt="Thumbnail of a person wearing a blue coat" />
          <img src="https://placehold.co/50x50" alt="Thumbnail of a person wearing a blue coat" />
          <img src="https://placehold.co/50x50" alt="Thumbnail of a person wearing a blue coat" />
          <FaShoppingCart />
        </div>
        <span>Preços ilustrativos</span>

        <div className={styles.details}>
          <div className={styles.info}>
            <div>
              <FaSyncAlt />
              <span>{product.name}</span>
            </div>
            <div>
              <span>REF:</span>
              <span>{product.reference}</span>
            </div>
            <div>
              <span>R$</span>
              <span>{price}</span>
            </div>
          </div>
          <div className={styles.price}>
            <div>
              <span>Atual</span>
              <div>
                <span>R$ {total}</span>
                <FaMinusCircle />
                <FaPlusCircle />
              </div>
            </div>
            <div>
              <span>Acumulado</span>
              <div>
                <span>R$ 0,00</span>
              </div>
            </div>
          </div>
        </div>
        <div className={styles.footer}>
          <div className={styles.size_selector}>
            <div className={styles.size_options}>
              {sizes.map((s) => (
                <div key={s.size} className={`${styles.size_option} ${s.size === size ? styles.selected : ""}`} onClick={() => setSize(s.size)}>
                  {s.quantity}
                  <span className={styles.size_quantity}>{s.size}</span>
                </div>
              ))}
            </div>
          </div>
          <div className={styles.pack}>
            <span>PACK</span>
            <span>18</span>
          </div>
        </div>
      </div>
    </>
  );
}
